<?php $secretkey = "xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx"; // Insert your reCaptcha secret key// -------------------------------------------------------------------------------------------------------------------------// No need to edit below this line -----------------------------------------------------------------------------------------// -------------------------------------------------------------------------------------------------------------------------$response = json_decode(file_get_contents("https://www.google.com/recaptcha/api/siteverify?secret=".$secretkey."&response=".$_POST["captcha"]."&remoteip=".$_SERVER["REMOTE_ADDR"]), true);function returnResult($status, $message){	$result = array(		'status' => $status,		'message' => $message	);	echo json_encode($result);	exit();}if ($response["success"] != false) {	$status = 1;	$message = "Captcha is correct";	returnResult($status, $message);} else {	$status = 0;	$message = "Captcha is not correct";	returnResult($status, $message);}?>